import { TFile } from "obsidian";

import IF from "./bigIF";

export async function templates(activeFile: any, type: string) {
    const result = {
        state: false,
        clusterSonTemplate: undefined as unknown as string,
        normalSonTemplate: undefined as unknown as string,
        brotherTemplate: undefined as unknown as string,
    }

    if (type == "clusterSon") {

        IF.ELSE(() => {
            const frontmatterProperties = this.app.metadataCache.getFileCache(activeFile).frontmatter;
            const isClusterTagExist = frontmatterProperties?.tags?.find((tag: string) => tag == "Cluster")
            const selfTagExist = frontmatterProperties?.tags?.find((tag: string) => tag == activeFile.basename)
            const generationFromActiveFileFrontmatterProperties = frontmatterProperties?.generation
            const generationFromActiveFilePath = (activeFile.path.match(/\//g) || []).length // how many "/" in the path
            const parentFileLink = frontmatterProperties?.parent

            IF.checkAll([
                [!frontmatterProperties, `Please set frontmatter properties`],
                [!activeFile.basename.endsWith("-cluster"), "cluster file must ends with '-cluster' word .\nexample :'lorens-cluster'"],
                [!((activeFile.path.match(/\//g) || []).length == 1), "The Cluster should not be in this generation."],
                [isClusterTagExist == undefined, "Set the 'Cluster' tag properly.\nadd 'Cluster' word to tags"],
                [selfTagExist == activeFile.basename, "Don't put the this note name as tag.\njust add 'Cluster' word to tags"],
                [generationFromActiveFileFrontmatterProperties === null, "Set the generation property properly.Not Null"],
                [generationFromActiveFileFrontmatterProperties == undefined, "Set the generation property properly."],
                [generationFromActiveFileFrontmatterProperties > 0, "The Cluster's generation property must be 0"],
                [generationFromActiveFileFrontmatterProperties < 0, "The Cluster's generation property must be 0"],
                [!(typeof (generationFromActiveFileFrontmatterProperties) === 'number'), "The Cluster's generation property must be Number.\nChange the property type to Number"],
                [parentFileLink != undefined, "The Cluster's main note should not have parent."],
            ])
            result.state = true
            result.clusterSonTemplate =
                `---
tags:
  - ${activeFile.basename.slice(0, -8).replace(/ /g, "-")}-cluster
parent: "[[${activeFile.path.slice(0, -3)}|${activeFile.basename}]]"
generation: ${generationFromActiveFilePath}
---
`

            return result
        })

    } else if (type == "normalSon") {

        IF.ELSE(() => {
            const frontmatterProperties = this.app.metadataCache.getFileCache(activeFile).frontmatter;
            const clusterTagName = activeFile.path.slice(9).replace(/\/.*$/, '').replace(/ /g, "-")
            const isClusterTagExist = frontmatterProperties?.tags?.find((tag: string) => tag == `${clusterTagName}`)
            const activeFileParentLink = `[[${activeFile?.parent?.path}|${activeFile?.parent?.name}]]`
            const sonFileParentLink = `[[${activeFile.path.slice(0, -3)}|${activeFile.basename}]]`
            const generationFromActiveFileFrontmatterProperties = frontmatterProperties?.generation
            const generationFromActiveFilePath = (activeFile.path.match(/\//g) || []).length - 1 // how many "/" in the path
            const parentFileLink = frontmatterProperties?.parent

            IF.checkAll([

                [!frontmatterProperties, `Please set frontmatter properties`],
                [isClusterTagExist == undefined, `You Should add '${clusterTagName}' tag which refers to the containing cluster.`],
                [parentFileLink === undefined, "Set the parent property."],
                [parentFileLink === null, "The parent property should'nt be empty."],
                [parentFileLink === "", "The parent property should'nt be empty."],
                [parentFileLink !== activeFileParentLink, `The parent property should be link to '${activeFile.parent?.name}' file.\nlike:${activeFileParentLink}`],
                [!(typeof (generationFromActiveFileFrontmatterProperties) === 'number'), "The Cluster's generation property must be Number.\nChange the property type to Number"],
                [generationFromActiveFileFrontmatterProperties == 0, "JUST Clusters's generation property must be 0"],
                [generationFromActiveFileFrontmatterProperties !== generationFromActiveFilePath, `The generation property must be ${generationFromActiveFilePath}`],
                [generationFromActiveFileFrontmatterProperties < 0, "The generation property shouldn't be a negative number "],
                [generationFromActiveFileFrontmatterProperties == undefined, "The generation property cant be empty."],

            ])
            result.state = true
            result.normalSonTemplate =
                `---
tags:
  - ${clusterTagName}
parent: "${sonFileParentLink}"
generation: ${generationFromActiveFilePath + 1}
---
`

            return result
        })
    } else if (type == "brother") {

        IF.ELSE(() => {
            const frontmatterProperties = this.app.metadataCache.getFileCache(activeFile).frontmatter;
            const clusterTagName = activeFile.path.slice(9).replace(/\/.*$/, '').replace(/ /g, "-")
            const isClusterTagExist = frontmatterProperties?.tags?.find((tag: string) => tag == `${clusterTagName}`)
            const activeFileParentLink = `[[${activeFile?.parent?.path}|${activeFile?.parent?.name}]]`
            const generationFromActiveFileFrontmatterProperties = frontmatterProperties?.generation
            const generationFromActiveFilePath = (activeFile.path.match(/\//g) || []).length - 1 // how many "/" in the path
            const parentFileLink = frontmatterProperties?.parent

            IF.checkAll([
                [!frontmatterProperties, `Please set frontmatter properties`],
                [isClusterTagExist == undefined, `You Should add '${clusterTagName}' tag which refers to the containing cluster.`],
                [parentFileLink === undefined, "Set the parent property."],
                [parentFileLink === null, "The parent property should'nt be empty."],
                [parentFileLink === "", "The parent property should'nt be empty."],
                [parentFileLink !== activeFileParentLink, `The parent property should be link to '${activeFile.parent?.name}' file.\nlike:${activeFileParentLink}`],
                [!(typeof (generationFromActiveFileFrontmatterProperties) === 'number'), "The Cluster's generation property must be Number.\nChange the property type to Number"],
                [generationFromActiveFileFrontmatterProperties == 0, "JUST Clusters's generation property must be 0"],
                [generationFromActiveFileFrontmatterProperties !== generationFromActiveFilePath, `The generation property must be ${generationFromActiveFilePath}`],
                [generationFromActiveFileFrontmatterProperties < 0, "The generation property shouldn't be a negative number "],
                [generationFromActiveFileFrontmatterProperties == undefined, "The generation property cant be empty."],

            ])
            result.state = true
            result.brotherTemplate =
                `---
tags:
  - ${clusterTagName}
parent: "${activeFileParentLink}"
generation: ${generationFromActiveFilePath}
---
`

            return result
        })
    }

    return result
}

//#region ORPHAN Template
export const orphanTemplate =
    `---
tags:
  - Orphan
---
`;
//#endregion

//#region Cluster template handling
export function clusterTemplate(): string {
    const clusterTemplate =
        `---
tags:
  - Cluster
generation: 0
---
`

    return clusterTemplate
}
//#endregion


